import {Header} from './layout/header'
import {Main} from './layout/Main'
import {Footer} from './layout/Footer'
import React, { Component } from "react";

class App extends Component {
  state = {
    search: [
      {
        "Title": "Scarface",
        "Year": "1983",
        "imdbID": "tt0086250",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BNjdjNGQ4NDEtNTEwYS00MTgxLTliYzQtYzE2ZDRiZjFhZmNlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg"
    },
    {
        "Title": "Scarface",
        "Year": "1932",
        "imdbID": "tt0023427",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BYmMxZTU2ZDUtM2Y1MS00ZWFmLWJlN2UtNzI0OTJiOTYzMTk3XkEyXkFqcGdeQXVyMjUxODE0MDY@._V1_SX300.jpg"
    },
    ],
  }

  componentDidMount() {
    fetch('http://www.omdbapi.com/?apikey=99cd179d&s=scarface&type=movie')
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log('в первый момент длина массива ' + this.state.search.length)
      console.log('в первый массив выглядит так: ' + this.state.search)
      this.setState({search: [data]})
      console.log('После обновления массив выглядит так: ' + this.state.search)
      console.log('После обновления длина массива ' +this.state.search.length)
    })
    .catch(error => {
      console.error('There was a problem with your fetch operation:', error);
    });
  }

  render() {
    return <>
            <Header />
            <Main 
              search={this.state.search}
              title={this.state.Title}
              year={this.state.Year}
              poster={this.state.Poster}
              />
            <Footer />
          </>
  }


}

export default App;
