import React from 'react'
import {Movies} from '../components/Movies'
import {Preloader} from '../components/Preloader';
import {Search} from '../components/Search';

class Main extends React.Component {
    state = {
        movies: [],
        testRow: ''
      }

      componentDidMount() {
        fetch(`http://www.omdbapi.com/?apikey=99cd179d&s=matrix`)
            .then(response => response.json())
            .then(data => this.setState({movies: data.Search}))
      }

      searchMovies = (str) => {
        fetch(`http://www.omdbapi.com/?apikey=99cd179d&s=${str}`)
            .then(response => response.json())
            .then(data => this.setState({movies: data.Search}))
        alert(str)
        alert('первый алерт '+ this.state.testRow)
        this.setState({testRow: str})
        alert('второй алерт '+ this.state.testRow)
      }

      componentDidUpdate(searchMovies) {
        searchMovies('iron man')
      }

    render() {
        return <main className="content container">
                    <Search searchMovies={this.searchMovies}/>
                    {
                        this.state.movies.length === 0 ?
                        <Preloader />
                        :
                        <Movies 
                        movies={this.state.movies}
                        />
                        
                    }         
                </main>
    }
}

export {Main}
