import React from "react";

class Search extends React.Component {
    state = {
        search: '',
    }

    handleKey = (event) => {
        if (event.key === 'Enter') {
            this.props.searchMovies(this.state.search)
        }
    }

    handleClick = () => {
        this.props.searchMovies(this.state.search)
    }

    render() {
        return <div className="row">
            <div className="col s12">
                <div className="row">
                    <div className="input-field">
                        <input
                            placeholder="Введите название фильма"
                            type="search"
                            className="validate"
                            value={this.state.search}
                            onChange={(e) => this.setState({ search: e.target.value })}
                            onKeyDown={this.handleKey} />
                        <button className="btn search-btn" onClick={this.handleClick}>Поиск</button>
                        
                        <label className="type-radio-label" htmlFor="All">Все категории
                            <input className="type-radio-input" type="radio" id="All" name="movie-type"/>
                        </label>
                        <label className="type-radio-label" htmlFor="Movies">Фильмы
                            <input className="type-radio-input" type="radio" id="Movies" name="movie-type"/>
                        </label>
                        <label className="type-radio-label" htmlFor="Series">Сериалы
                            <input className="type-radio-input" type="radio" id="Series" name="movie-type"/>
                        </label>
                    </div>
                </div>
            </div>
        </div>
    }
}

export { Search }
