import {Header} from './layout/header'
import {Main} from './layout/Main'
import {Footer} from './layout/Footer'
import React, { Component } from "react";

class App extends Component {
  state = {
    movies: [
      {
        "Title": "Scarface",
        "Year": "1983",
        "imdbID": "tt0086250",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BNjdjNGQ4NDEtNTEwYS00MTgxLTliYzQtYzE2ZDRiZjFhZmNlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg"
    },
    {
        "Title": "Scarface",
        "Year": "1932",
        "imdbID": "tt0023427",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BYmMxZTU2ZDUtM2Y1MS00ZWFmLWJlN2UtNzI0OTJiOTYzMTk3XkEyXkFqcGdeQXVyMjUxODE0MDY@._V1_SX300.jpg"
    },
    ],
  }

  componentDidMount() {
    alert(1);
  }

  render() {
    return <>
            <Header />
            <Main 
              movies={this.state.movies}
              title={this.state.Title}
              year={this.state.Year}
              poster={this.state.Poster}
              />
            <Footer />
          </>
  }


}

export default App;
